import React, { useState } from 'react';

function CardFront({ word, flipCard }) {
  return (
    <div className="card front" onClick={flipCard}>
      <h2>{word}</h2>
    </div>
  );
}

function CardBack({ word, meaning, examples, flipCard }) {
  return (
    <div className="card back" onClick={flipCard}>
      <h2>{word}</h2>
      <p>
        <strong>Meaning:</strong> {meaning}
      </p>
      <p>
        <strong>Examples:</strong>
      </p>
      <ul>
        {examples.map((example, index) => (
          <li key={index}>{example}</li>
        ))}
      </ul>
    </div>
  );
}

function Card({ word, meaning, examples }) {
  const [isFlipped, setIsFlipped] = useState(false);

  const flipCard = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div
      className={`card-container ${isFlipped ? 'flipped' : ''}`}
      onClick={flipCard}
    >
      <CardFront word={word} flipCard={flipCard} />
      <CardBack
        word={word}
        meaning={meaning}
        examples={examples}
        flipCard={flipCard}
      />
    </div>
  );
}

export default Card;
