import React, { useState, useEffect } from 'react';
import { ToastContainer, toast, Bounce } from 'react-toastify';
import { NavLink } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';
import './LearnNewWords.css';
import axios from 'axios';
import Card from './Card.mjs';
import origin from '../config/config.mjs';

const wordsLimit = 5;

function LearnNewWords() {
  console.log('start');

  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    console.log('useEffect');
    fetchWords();
  }, []);

  const fetchWords = async () => {
    console.log('fetching words');
    try {
      const response = await axios.get(`${origin}/api/words`);
      setWords(response.data);
      setCurrentWordIndex(0);
    } catch (error) {
      console.error('Error fetching words:', error);
    }
  };

  const resetWord = async (index, resetWordId, word) => {
    try {
      const existingWords = [...words];
      const response = await axios.post(`${origin}/api/anotherword`, {
        words: existingWords,
        wordId: resetWordId?.toString(),
      });
      if (response.data) {
        toast.info(`Word '${word}' will no longer be shown!`, {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
        const newWords = words.toSpliced(index, 1, response.data);
        setWords(newWords);
      }
    } catch (error) {
      console.error('Error fetching words:', error);
    }
  };

  const learnWord = () => {
    console.log(currentWordIndex);
    setCurrentWordIndex((prevIndex) => prevIndex + 1);
  };

  const addWordToCollection = async (word) => {
    try {
      const response = await axios.post(`${origin}/api/word`, {
        word: {
          word: word.word,
          meaning: word.meaning,
          wordIdFromCommonDB: word._id,
        },
      });
      if (response.data.added) {
        toast.success(`Word '${word.word}' added!`, {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
      } else {
        toast.error(`Word '${word.word}' didn't add!`, {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
      }
    } catch (error) {
      console.error('Error adding word to collection:', error);
    }
  };

  const currentWord = words[currentWordIndex] || null;

  return (
    <div className="learn-new-words">
      {currentWordIndex < wordsLimit ? (
        <>
          <ToastContainer />
          <Card
            word={currentWord?.word}
            meaning={currentWord?.meaning}
            id={currentWord?._id}
          />
          <div className="button-container">
            <button
              className="button"
              onClick={() => {
                addWordToCollection(currentWord);
                learnWord();
              }}
            >
              Start learning this word
            </button>
            <button
              className="button secondary"
              onClick={() => {
                resetWord(currentWordIndex, currentWord._id, currentWord.word);
              }}
            >
              I already know this word
            </button>
          </div>
        </>
      ) : (
        <>
          <p className="completion-message">Nice job!</p>
          <NavLink to="/" className="nav-link">
            <span className="nav-icon">üè†</span>
            <div className="nav-title">Main</div>
          </NavLink>
          <NavLink to="/review" className="nav-link">
            <span className="nav-icon">‚Üª</span>
            <div className="nav-title">Review words</div>
          </NavLink>
        </>
      )}
    </div>
  );
}

export default LearnNewWords;
