import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './components/Card.mjs';
import './styles.css';

function App() {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isReachedEnd, setIsReachedEnd] = useState(false);

  useEffect(() => {
    fetchWords();
  }, []);

  const fetchWords = () => {
    axios
      .get('http://localhost:5000/api/word')
      .then((res) => {
        setWords(res.data);
        setCurrentWordIndex(0);
      })
      .catch((error) => console.error('Error fetching words:', error));
  };

  const resetWord = (id) => {
    // words.indexOf
  };

  const nextWord = () => {
    setCurrentWordIndex((prevIndex) => prevIndex + 1);
  };

  const addWordToCollection = (id) => {
    axios
      .post('http://localhost:5000/api/word', { id })
      .then((res) => console.log('Word added to collection:', res.data))
      .catch((error) =>
        console.error('Error adding word to collection:', error)
      );
  };
  const currentWord = words[currentWordIndex] || {};

  return (
    <div className="app">
      <Card
        word={currentWord.word}
        meaning={currentWord.meaning}
        id={currentWord._id}
      />
      <button
        onClick={() => {
          nextWord();
          addWordToCollection(currentWord._id);
        }}
      >
        Next word
      </button>
      <button onClick={() => resetWord(currentWord._id)}>
        Reset this word
      </button>
    </div>
  );
}

export default App;
