import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './Card.mjs'; // Компонент карточки
import './styles.css';

function App() {
  const [word, setWord] = useState({});

  useEffect(() => {
    fetchWords();
  }, []);

  const fetchWords = () => {
    axios
      .get('http://localhost:5000/api/word')
      .then((res) => {
        res.data.forEach((objOfWord) => {
          setWord(objOfWord);
        });
      })
      .catch((error) => console.error('Error fetching words:', error));
  };

  return (
    <div className="app">
      <Card word={word.word} meaning={word.meaning} />
      <button onClick={fetchWords} className="next-button">
        Next
      </button>
    </div>
  );
}

export default App;
