import React, { useState, useEffect } from 'react';
import { ToastContainer, toast, Bounce } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import Card from './Card.mjs';
import '../styles.css';
import origin from '../config/config.mjs';

const wordsLimit = 5;

function LearnNewWords() {
  console.log('start');

  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    console.log('useEffect');
    fetchWords();
  }, []);

  const fetchWords = async () => {
    console.log('fetching words');
    try {
      const response = await axios.get(`${origin}/api/words`);
      setWords(response.data);
      setCurrentWordIndex(0);
    } catch (error) {
      console.error('Error fetching words:', error);
    }
  };

  const resetWord = async (index, resetWordId) => {
    try {
      const existingWords = [...words];
      const response = await axios.post(`${origin}/api/anotherword`, {
        words: existingWords,
        wordId: resetWordId?.toString(),
      });
      if (response.data) {
        toast.info('This word will no longer be shown!', {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
        const newWords = words.toSpliced(index, 1, response.data);
        setWords(newWords);
      }
    } catch (error) {
      console.error('Error fetching words:', error);
    }
  };

  const learnWord = () => {
    console.log(currentWordIndex);
    setCurrentWordIndex((prevIndex) => prevIndex + 1);
  };

  const addWordToCollection = async (word) => {
    try {
      const response = await axios.post(`${origin}/api/word`, {
        word: {
          word: word.word,
          meaning: word.meaning,
          wordIdFromCommonDB: word._id,
        },
      });
      if (response.data.added) {
        toast.success(`Word ${word} added!`, {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
      } else {
        toast.error(`Word ${word} didn't add!`, {
          position: 'top-right',
          autoClose: 2000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'light',
          transition: Bounce,
        });
      }
    } catch (error) {
      console.error('Error adding word to collection:', error);
    }
  };

  const currentWord = words[currentWordIndex] || null;

  return (
    <div className="app">
      {currentWordIndex < wordsLimit ? (
        <>
          <ToastContainer />
          <Card
            word={currentWord?.word}
            meaning={currentWord?.meaning}
            id={currentWord?._id}
          />
          <button
            onClick={() => {
              addWordToCollection(currentWord);
              learnWord();
            }}
          >
            Start learning this word
          </button>

          <button
            onClick={() => {
              resetWord(currentWordIndex, currentWord._id);
            }}
          >
            I already know this word
          </button>
        </>
      ) : (
        <p>Nice job!</p>
      )}
    </div>
  );
}

export default LearnNewWords;
