import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './Card.mjs'; // Компонент карточки
import './styles.css';

function App() {
  const [word, setWord] = useState({});

  useEffect(() => {
    fetchWords();
  }, []);

  const fetchWords = async () => {
    try {
      const response = await axios.get('api/word');
      setWord(response.data);
    } catch (error) {
      console.error('Error fetching words:', error);
    }
  };

  return (
    <div className="app">
      <Card word={word.word} meaning={word.meaning} examples={word.examples} />
      <button onClick={fetchWords} className="next-button">
        Next
      </button>
    </div>
  );
}

export default App;
