import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './components/Card.mjs';
import './styles.css';

function App() {
  const [words, setWords] = useState([]);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);

  useEffect(() => {
    fetchWords();
    resetWord(currentWordIndex);
  }, []);

  const fetchWords = () => {
    axios
      .get('http://localhost:5000/api/words')
      .then((res) => {
        setWords(res.data);
        setCurrentWordIndex(0);
      })
      .catch((error) => console.error('Error fetching words:', error));
  };

  const resetWord = (index) => {
    axios
      .get('http://localhost:5000/api/word')
      .then((res) => {
        const newWords = [...words];
        newWords.splice(index, 1, res.data);
        setWords(newWords);
      })
      .catch((error) => console.error('Error fetching words:', error));
  };

  const nextWord = () => {
    setCurrentWordIndex((prevIndex) => {
      if (prevIndex < words.length - 1) {
        return prevIndex + 1;
      } else {
        return prevIndex;
      }
    });
  };

  const addWordToCollection = (word) => {
    axios
      .post('http://localhost:5000/api/word', {
        word: {
          word: word.word,
          meaning: word.meaning,
          wordIdFromCommonDB: word._id,
        },
      })
      .then((res) => console.log('Word added to collection:', res.data))
      .catch((error) =>
        console.error('Error adding word to collection:', error)
      );
  };
  const currentWord = words[currentWordIndex] || {};

  return (
    <div className="app">
      {currentWordIndex < 5 ? (
        <>
          <Card
            word={currentWord.word}
            meaning={currentWord.meaning}
            id={currentWord._id}
          />
          <button
            onClick={() => {
              addWordToCollection(currentWord);
              nextWord();
            }}
          >
            Next word
          </button>
          <button
            onClick={() => {
              resetWord(currentWordIndex);
            }}
          >
            Reset this word
          </button>
        </>
      ) : (
        <p>Nice job!</p>
      )}
    </div>
  );
}

export default App;
